package book.artconcurrent.ch08;import java.util.concurrent.BrokenBarrierException;import java.util.concurrent.CyclicBarrier;/** */public class CyclicBarrierDemo3 {    private static CyclicBarrier barrier = new CyclicBarrier(2);    public static void main(String[] args) {        Thread thread = new Thread(() -> {            try {                barrier.await();            } catch (InterruptedException | BrokenBarrierException e) {                e.printStackTrace();            }        });        thread.start();        thread.interrupt();        try {            barrier.await();        } catch (Exception e) {            System.out.println(barrier.isBroken());        }    }}